import React, { useContext } from "react";
import { Context } from "../../../utils/context";

const CategoryItem = ({ item }) => {
  const [{}, dispatch] = useContext(Context);
  const colorArray = ['#fcd200','#00cpb8','#c997c6','#e2f0cB']


  return (
    <div className="card-ipad w-full md:w-1/2 lg:my-4 md:mx-2 lg:w-1/6">
      <article className="bg-white overflow-hidden rounded-lg shadow-lg p-2"
      style={{backgroundColor:"#1F4B99"}}>
        <img
          alt={item.title}
          className="block h-auto"
          src={item.image}
        />
        <header className="flex items-center flexible justify-center leading-tight p-1 md:p-3">
          <h1 className="text-lg">{item.name}</h1>
        </header>
      </article>
    </div>
  );
};

export default React.memo(CategoryItem);
